mapper.select:
SELECT *
FROM (
  SELECT o.id,
         ROW_NUMBER() OVER (ORDER BY o.order_date DESC, o.id DESC) rn
  FROM orders o
  WHERE 1=1

      AND o.order_status = #{cri.orderStatus}


      AND o.customer_name LIKE '%' || #{cri.keyword} || '%'

) t
WHERE rn > (#{cri.pageNum} - 1) * #{cri.amount}
  AND rn <= #{cri.pageNum} * #{cri.amount}

SELECT o.id, o.user_id, o.orders_id, TO_CHAR(o.order_date, 'YYYY-MM-DD') AS order_date, o.customer_name AS receiver, o.order_status AS order_status_main, o.address, o.phone_number, o.delivery_msg, o.payment_method, o.zipcode, o.total_price, op.id AS orders_product_id, op.product_id, op.quantity, op.order_status AS order_status_product, p.product_name, p.product_price, p.product_content, p.discount_price, ip.img_path_thumb AS img_path_thumb FROM orders o LEFT JOIN orders_product op ON o.id = op.orders_id LEFT JOIN product p ON op.product_id = p.id LEFT JOIN img_path ip ON ip.product_id = p.id AND ip.is_thumbnail_main = 1 WHERE o.id IN
#{orderId}
ORDER BY o.order_date DESC, o.id DESC, op.id

mapper.update:
UPDATE orders_product
SET order_status = #{orderStatus}
WHERE id = #{ordersProductId}

UPDATE orders
SET order_status = #{orderStatus}
WHERE orders_id = #{ordersId}

mapper.select:
SELECT COUNT(*) FROM orders WHERE 1=1
AND order_status = #{cri.orderStatus}
AND customer_name LIKE '%' || #{cri.keyword} || '%'
